	(257, '_transient_misha_upgrade_meta505', 'a:6:{s:7:"headers";O:42:"Requests_Utility_CaseInsensitiveDictionary":1:{s:7:"\0*\0data";a:8:{s:13:"last-modified";s:29:"Tue, 29 Aug 2017 02:47:16 GMT";s:12:"content-type";s:16:"application/json";s:14:"content-length";s:4:"5636";s:4:"date";s:29:"Fri, 07 Sep 2018 11:31:42 GMT";s:13:"accept-ranges";s:5:"bytes";s:6:"server";s:9:"LiteSpeed";s:25:"strict-transport-security";s:44:"max-age=10886400; includeSubDomains; preload";s:7:"alt-svc";s:37:"quic=":443"; ma=2592000; v="35,39,43"";}}s:4:"body";s:5636:"{\n    	"name" : "True Metabox",\n    	"slug" : "true-metabox",\n    	"homepage" : "https://rudrastyh.com/plugins/meta-boxes-options-pages",\n    	"download_url" : "https://rudrastyh.com/plg_api_upd/meta505/true-metabox.zip",\n    	"version" : "2.3",\n    	"requires" : "3.0",\n    	"tested" : "4.8.1",\n    	"last_updated" : "2017-08-20 00:05:00",\n		"upgrade_notice" : "Plugin update is available.",\n		"author" : "Misha Rudrastyh",\n		"author_homepage" : "https://rudrastyh.com",\n		"sections" : {\n			"description" : "This plugin allows you to create metaboxes for posts, settings for taxonomy terms and custom option pages without affecting the website performance!",\n			"installation" : "Upload the plugin to your blog, Activate it, that\'s it!",\n			"changelog" : "<h4>2.3 – August 20, 2017</h4><ul><li>Added: Multiple taxonomies are now supported by Select2 dropdown lists.</li><li>Fixed: Bug with multiple post type selection in option pages.</li><li>The plugin update mechanism is improved.</li></ul><h4>2.1 – May 16, 2017</h4><ul><li>Added: Swedish translation (thanks to Fredric Österlund).</li><li>Select2 fields are now supported by taxonomies and option pages.</li></ul><h4>2.0 – May 8, 2017</h4><ul><li>Added: New fields that allow to search and select posts (any post type) and terms (any taxonomy). Multiple selection is supported.</li><li>Chosen script replaced with Select2.</li></ul><h4>1.1 – June 9, 2016</h4><ul><li>Added: Google Maps are now in the same language as the website.</li><li>Improved: Reduced the number of SQL queries in Dashboard.</li></ul><h4>1.0</h4><ul><li>Plugin release date.</li></ul>",\n			"screenshots" : "<ol><li><a href=\'https://rudrastyh.com/wp-content/uploads/2016/07/metabox-with-default-inputs.png?rev=1469094346\' target=\'_blank\'><img src=\'https://rudrastyh.com/wp-content/uploads/2016/07/metabox-with-default-inputs.png?rev=1469094346\' alt=\'WordPress Metabox with common input fields - text, textarea, checkbox, select and radio buttons.\' /></a><p>WordPress Metabox with common input fields - text, textarea, checkbox, select and radio buttons.</p></li><li><a href=\'https://rudrastyh.com/wp-content/uploads/2016/07/wordpress-tiny-mce-html-editor.png?rev=1491558211\' target=\'_blank\'><img src=\'https://rudrastyh.com/wp-content/uploads/2016/07/wordpress-tiny-mce-html-editor.png?rev=1491558211\' alt=\'TinyMCE and HTML (quicktags) editor for your metabox and option page.\' /></a><p>TinyMCE and HTML (quicktags) editor for your metabox and option page.</p></li><li><a href=\'https://rudrastyh.com/wp-content/uploads/2016/07/upload-image-field.png?rev=1491558532\' target=\'_blank\'><img src=\'https://rudrastyh.com/wp-content/uploads/2016/07/upload-image-field.png?rev=1491558532\' alt=\'Upload Image for your posts, terms of option pages.\' /></a><p>Upload Image for your posts, terms of option pages.</p></li><li><a href=\'https://rudrastyh.com/wp-content/uploads/2016/07/file-upload-field.png?rev=1491558822\' target=\'_blank\'><img src=\'https://rudrastyh.com/wp-content/uploads/2016/07/file-upload-field.png?rev=1491558822\' alt=\'Upload files for WordPress metaboxes and option pages.\' /></a><p>Upload files for WordPress metaboxes and option pages.</p></li><li><a href=\'https://rudrastyh.com/wp-content/uploads/2016/07/video-with-preview.jpg?rev=1491559197\' target=\'_blank\'><img src=\'https://rudrastyh.com/wp-content/uploads/2016/07/video-with-preview.jpg?rev=1491559197\' alt